import screenshot from 'screenshot-desktop'
import fs from 'fs'

export const getScreenshot = () => {
    return screenshot({ format: 'png' }).then((img) => {
        // img: Buffer filled with jpg goodness
        fs.writeFile(`C:/Users/Brian/Desktop/TFTracker/TftTrackerCore/tfttracker-backend/tftproject/screenshot/image.png`, img, () =>
        console.log('ninja moment'));
    }).catch((err) => {
        console.log(err)
    })
}
