import React from "react";
import { useEffect, useState } from "react";
import { Card, ProgressBar } from "react-bootstrap";
import EnemyIcon from "../EnemyIcon/EnemyIcon";
import enterKey from "../../images/enter-key.png";
import TFTPoro from "../../images/TFT-Poro.png";
import "./MainDisplay.css";

const axios = require("axios").default;

const MainDisplay = () => {
  const [username, setUsername] = useState("valorantprodigy");
  const [local_url, setLocal_url] = useState("http://127.0.0.1:8000/");

  const [isLoading, setIsLoading] = useState(false);
  const [selectButton, setSelectButton] = useState(0); //Load appropriate button
  const [selectButtonText, setSelectButtonText] = useState([
    "Initalize Enemies",
    "Find Enemy",
    "Update Enemies",
  ]);

  //Variables to pass between api calls

  const [enemyPlayed, setEnemyPlayed] = useState("");
  const [enemyTracker, setEnemyTracker] = useState([]);
  //this is returned from updateSidebarPlayers, will use to display 1-4 enemies to watchout for
  const [enemiesToWatch, setEnemiesToWatch] = useState([]);

  /*add functionality later
  //ALT KEY: GETSIDEBARPLAYERS() INITAL FUNCTION
  //ENTER KEY: GETENEMYAGAINST() => UPDATESIDEBARPLAYERS
  window.addEventListener("keydown", (event) => {
    if (event.key === "Enter") {
      console.log("ryan");
      //initalizeEnemies()
    } else if (event.key === "Alt") {
      console.log("rwywawn");
      //findAndUpdateEnemy()
    }
  });*/

  const selectButtonFunction = () => {
    switch (selectButton) {
      case 0:
        initalizeEnemies();
        break;
      case 1:
        findEnemy();
        break;
      case 2:
        updateEnemies();
        break;
    }
  };

  const initalizeEnemies = () => {
    setIsLoading(true);
    axios
      .get(local_url + "enemies/sidebar/", {
        /* Parameter to send */
      })
      .then(function (response) {
        // handle success
        setIsLoading(false);
        setSelectButton(1);
        console.log(response);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .then(function () {
        console.log("brian");
        // always executed
      });
  };

  const findEnemy = () => {
    setIsLoading(true);
    axios
      .get(local_url + "enemies/gameboard/", {
        params: JSON.stringify({
          username: username,
        }),
      })
      .then(function (response) {
        console.log(response);
        setEnemyPlayed(response.data.enemyPlayed);
        setIsLoading(false);
        setSelectButton(2);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  };

  const updateEnemies = () => {
    setIsLoading(true);
    axios
      .get(local_url + "enemies/update/", {
        params: JSON.stringify({
          enemyPlayed: enemyPlayed,
          enemyTracker: enemyTracker,
        }),
      })
      .then(function (response) {
        setIsLoading(false);
        setSelectButton(0);
        console.log(response);
        setEnemyTracker(response.data.playingOrder);
        setEnemiesToWatch(response.data.enemies);
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      });
  };

  return (
    <div className="app-container">
      {
        //<div className="title">TFTinder</div>
      }
      {isLoading ? (
        <></>
      ) : (
        <>
          <div className="subtitle1">Up Next!</div>
          <div className="warning-container">
            {/*enemiesToWatch &&
              enemiesToWatch.map((enemy) => (
                <EnemyIcon enemy={enemy}/>
              ))*/}
            <EnemyIcon />
          </div>
          <div className="subtitle2">Recently Played</div>
          <div className="played-container">
            {enemyTracker && enemyTracker.map((enemy) => <div>{enemy}</div>)}
          </div>
          <div className="button-container">
            <button onClick={() => selectButtonFunction()}>
              {selectButtonText[selectButton]}
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default MainDisplay;
