import './App.css';
import { useEffect, useState } from 'react'
import { getSidebarPlayers, updateSidebarPlayers, getEnemyAgainst } from './services/api-calls'
import enterKey from './images/enter-key.png'
const axios = require('axios').default;

//serializer should send:

//ADD LOADER WHEN API IS BEING CALLED
const App = () => {

  const [username, setUsername] = useState('birdnest')
  //these are variables to get from api calls and to send to other api calls
  const [enemyTracker, setEnemyTracker] = useState([])
  //this is returned from updateSidebarPlayers, will use to display 1-4 enemies to watchout for 
  const [enemiesToWatch, setEnemiesToWatch] = useState([])
  const [local_url, setLocal_url] = useState('http://127.0.0.1:8000/')

  //ALT KEY: GETSIDEBARPLAYERS() INITAL FUNCTION
  //ENTER KEY: GETENEMYAGAINST() => UPDATESIDEBARPLAYERS
  window.addEventListener('keydown', (event) => {
    if (event.key === 'Enter') {
      console.log('ryan')
      //initalizeEnemies()  
    }
    else if (event.key === 'Alt') {
      console.log('rwywawn')
      //findAndUpdateEnemy()
    }
  })

  const initalizeEnemies = () => {
    getSidebarPlayers()
  }

  const findAndUpdateEnemy = () => {
    axios.get(local_url + 'enemies/gameboard/', {/* Parameter to send */ })
      .then(function (response) {
        let enemyPlayed = response.enemyPlayed
        axios.get(local_url + 'enemies/update/', {enemyPlayed:enemyPlayed, enemyTracker:enemyTracker})
          .then(function (response) {
            console.log(response);
            setEnemyTracker(response.playingOrder)
            setEnemiesToWatch(response.enemies)
          })
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
  }

  return (
    <div className='app-container'>
      {
        //THIS IS TEXT TELLING USER TO PRESS ENTER
        <div className='text-container'>
          <div>Press Enter</div>
          <img className='enter-key' src={enterKey} />
        </div>
      }
      <button onClick={() => initalizeEnemies()}>initalizeEnemies</button>
      <button onClick={() => findAndUpdateEnemy()}>findAndUpdateEnemy</button>
      {
        enemiesToWatch && enemiesToWatch.map(enemy => (
          <div>enemy.name</div>
        ))}
    </div>
  );
}

export default App;
