import './App.css';
import { useEffect, useState } from 'react'
import { getSidebarPlayers, updateSidebarPlayers, getEnemyAgainst } from './services/api-calls'
import enterKey from './images/enter-key.png'
const axios = require('axios').default;

//serializer should send:

//ADD LOADER WHEN API IS BEING CALLED
const App = () => {

  const [username, setUsername] = useState('valorantprodigy')
  const [local_url, setLocal_url] = useState('http://127.0.0.1:8000/')

  const [isLoading, setIsLoading] = useState(false)

  //these are variables to get from api calls and to send to other api calls
  const [enemyTracker, setEnemyTracker] = useState([])
  //this is returned from updateSidebarPlayers, will use to display 1-4 enemies to watchout for 
  const [enemiesToWatch, setEnemiesToWatch] = useState([])

  const [enemyPlayed, setEnemyPlayed] = useState('')  

  //ALT KEY: GETSIDEBARPLAYERS() INITAL FUNCTION
  //ENTER KEY: GETENEMYAGAINST() => UPDATESIDEBARPLAYERS
  window.addEventListener('keydown', (event) => {
    if (event.key === 'Enter') {
      console.log('ryan')
      //initalizeEnemies()  
    }
    else if (event.key === 'Alt') {
      console.log('rwywawn')
      //findAndUpdateEnemy()
    }
  })

  const initalizeEnemies = () => {
    getSidebarPlayers()
  }

  const findAndUpdateEnemy = () => {
    axios.get(local_url + 'enemies/gameboard/', {
      params: JSON.stringify({
        username: username,
      })
    })
      .then(function (response) {
        console.log(response)
        setEnemyPlayed(response.data.enemyPlayed)
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
  }

  const updateEnemies = () => {
    axios.get(local_url + 'enemies/update/', {
      params: JSON.stringify({
        enemyPlayed: enemyPlayed,
        enemyTracker: enemyTracker,
      })
    })
      .then(function (response) {
        console.log(response);
        setEnemyTracker(response.data.playingOrder)
        setEnemiesToWatch(response.data.enemies)
      })
  }

  return (
    <div className='app-container'>
      {
        //THIS IS TEXT TELLING USER TO PRESS ENTER
        <div className='text-container'>
          <div>Press Enter</div>
          <img className='enter-key' src={enterKey} />
        </div>
      }
      <div className='button-container'>
        <button onClick={() => initalizeEnemies()}>initalizeEnemies</button>
        <button onClick={() => findAndUpdateEnemy()}>findEnemy</button>
        <button onClick={() => updateEnemies()}>updateEnemies</button>
      </div>
      <div className='warning-container'>
        <div>Watch Out For:</div>
        {enemiesToWatch && enemiesToWatch.map(enemy => (
          <div>{enemy.username} {enemy.health_points}</div>
        ))}
      </div>
      <div className='played-container'>
        <div>Played:</div>
        {enemyTracker && enemyTracker.map(enemy => (
          <div>{enemy}</div>
        ))}
      </div>
    </div>
  );
}

export default App;
